// Custom Node.js

function saveFileContents(rawData) {
 // console.log(rawData)
  const nombreReporte = "solicitud.zip"

  const fileURL = `data:application/zip;base64,${rawData}`
 // console.log(fileURL)
  const fileLink = document.createElement('a')
  fileLink.href = fileURL
  fileLink.setAttribute('download', `${nombreReporte}`)
  document.body.appendChild(fileLink)
  fileLink.click()
}

function mainDownload(contextBtn) {
  console.log("entro a mainDownload")
  console.log(contextBtn)
  console.log("fin")
  const idBoton = contextBtn.id
  const indexOfBtn = idBoton.indexOf(".")+1
  const positionIndex = idBoton.length - indexOfBtn
  const indexFinal = idBoton.substring(indexOfBtn, indexOfBtn+positionIndex)
  console.log(idBoton,indexOfBtn,positionIndex,indexFinal)


  // Obtiene el nombre del archivo de la tabla
  const solicitud = get('documento.'+indexFinal)
  //   console.log("El nombre es:",fileName)
  //   const subIndex = solicitud.indexOf('.')+1
  //   console.log("Este es el subIndex",subIndex)
  //   const positionFileName = solicitud.length - subIndex
  //   //const extensionFileName = solicitud.substring(subIndex, subIndex+positionFileName)
  //  // console.log("Esta es la extension",extensionFileName)
  var ajaxRequest = new pui.AjaxRequest({
    "method": "get",
    "url": `https://profoundjs.com/run/jdeleon/kywi/wsapi/file-download?idsolicitud=${solicitud}`,
    "onsuccess": function (feedback) {
      saveFileContents(feedback.getResponseText());
    },
    "onfail": function (feedback) {
      alert("Error descargando archivo: " + feedback.getResponseText());
    }
  });
  ajaxRequest.send();
}

mainDownload(this);

